import React from "react";
import axios from "axios";
export default class Posts extends React.Component {

  
  constructor() {
    super();
    this.state = {
        username: "",
        check1:0,
        check2:0,
        check3:0,
        mData: [{username: "ABC", check1: "1", check2: "0", check3: "1" }, {username: "XYZ", check1: "0", check2: "0", check3: "1" }, {username: "123", check1: "1", check2: "0", check3: "0" }, ]
    };
    
  }

  componentDidMount(){

  }


  onChange = (e) => {
    // Because we named the inputs to match their corresponding values in state, it's
    // super easy to update the state
    const state = this.state
    state[e.target.name] = e.target.value;
    this.setState(state);
    console.log(state);
  }

  onClickuser = (e) => {
    // Because we named the inputs to match their corresponding values in state, it's
    // super easy to update the state
    const username = this.state.username
    var data = this.state.mData;
    for(var k in data){
        var obj = data[k];
        if(obj.username === username){
            console.log("Found");
            this.setState({check1: obj.check1, check2: obj.check2, check3: obj.check3})
        }else{
            console.log("Available");
        }
    }
  }

  
  onChangecb = (e) => {
    const state = this.state
    if (e.target.checked) {
      state[e.target.name] = e.target.value;
    } else {
      state[e.target.name] = 0;
    }
    this.setState(state);
  }

    
handleUpdatedata = (id,e) => {
  e.preventDefault();
  
}

    
onSubmit = (e) => {
    e.preventDefault();
    const { username,check1,check2,check3  } = this.state;
    var temp_ar = {check1: check1, check2: check2, check3: check3}
    const fData = this.state.mData.map((data, sidx) => {
      if (data.username !== username) return data;
      return { ...data, ...temp_ar};
    });
    console.log(fData);
    this.setState({ mData: fData, username: "",check1:0,check2:0,check3:0 });
    alert("Successfully Saved");
  }

onCancel = (e) => {
    e.preventDefault();
    this.setState({username: "",check1:0,check2:0,check3:0})
  }
  render() {
    const { username,check1,check2,check3 } = this.state;

    var submit_button = "";
    var header_name = "";
    if(this.state.update === 'yes'){
      submit_button = <button type="submit" className="btn btn-success">Update</button>;
      //header_name = "Update Planning";
    }else{
      submit_button = <button type="submit" className="btn btn-primary">Submit</button>;
      header_name = "View BOQ";
    }
    
    //console.log(this.state.datatable_data);
    return (
      <div id="content">
        
        <form id="smart-form-register" className="smart-form" noValidate="novalidate" onSubmit={this.onSubmit}>

                        <fieldset>
                            <section className="col col-3">
                                <label className="label">Depot</label>
                                <label className="input">
                                {" "}
                                <input type="text" name="username" value={username} placeholder="Username"  onChange={this.onChange} />
                                {" "}
                                </label> 
                                <span onClick={(e) => this.onClickuser(e)} style={{fontSize: "12px", backgroundColor: "#008116", color: "white", padding: "3px", borderRadius: "3px", cursor: "pointer"}}>Check User</span>                             
                            </section>

                            <section>
                                <label className="toggle">
                                    <input type="checkbox" name="check1" value={1} checked={check1 > 0 ? true : false} onChange={this.onChangecb} />
                                    Check 1
                                </label>
                            </section>

                            <section>
                                <label className="toggle">
                                    <input type="checkbox" name="check2" value={1} checked={check2 > 0 ? true : false} onChange={this.onChangecb} />
                                    Check 2
                                </label>
                            </section>

                            <section>
                                <label className="toggle">
                                    <input type="checkbox" name="check3" value={1} checked={check3 > 0 ? true : false} onChange={this.onChangecb} />
                                    Check 3
                                </label>
                            </section>
                        
                        </fieldset>
                        

                        
                        <footer>
                          <button type="submit"  style={{fontSize: "12px", backgroundColor: "#0081ff", color: "white", padding: "3px", borderRadius: "3px", cursor: "pointer", border: "none"}}>Submit</button>
                          &nbsp;&nbsp;
                          <span type="reset" onClick={(e) => this.onCancel(e)}  style={{fontSize: "12px", backgroundColor: "#8b8b8b", color: "white", padding: "3px", borderRadius: "3px", cursor: "pointer"}}>Cancel</span>
                        </footer>
                      </form>

      </div>
    );
  }
}
