}

    
onSubmit = (e) => {
    e.preventDefault();
    alert("Some error occured");
  }


  render() {
    const { username } = this.state;

    var submit_button = "";
    var header_name = "";
    if(this.state.update === 'yes'){
      submit_button = <button type="submit" className="btn btn-success">Update</button>;
      //header_name = "Update Planning";
